package com.ecommercecop.controller;

import java.io.IOException;
import java.util.Optional;
import org.slf4j.*;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PathVariable;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.multipart.MultipartFile;

import com.ecommercecop.model.Producto;
import com.ecommercecop.model.Usuarios;
import com.ecommercecop.service.ProductoServicio;
import com.ecommercecop.service.SubirDocServicios;
import com.ecommercecop.service.UsuarioServicio;
import com.ecommercecop.service.UsuarioServicioImplement;

import jakarta.servlet.http.HttpSession;

@Controller
@RequestMapping("/productos")
public class ProductoControlador {
	
	private final Logger LOGGER = LoggerFactory.getLogger(ProductoControlador.class);
	
	@Autowired
	private ProductoServicio productoServicio;
	
	@Autowired
	private SubirDocServicios subir;
	
	@Autowired
	private UsuarioServicio usuarioServicio;
	
	@GetMapping("")
	public String Mostrar(Model modelo) {
		modelo.addAttribute("productos", productoServicio.findAll());
		return "productos/mostrar";
		}
	
	@GetMapping("/crear")
	public String crear() {
		return "productos/crear";                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
	}
	@PostMapping("/guardar")
	public String guardar(Producto productos,@RequestParam("img") MultipartFile archivo, HttpSession session) throws IOException {
		LOGGER.info("Esto nos muestra el producto{}",productos);
	
		
		Usuarios u= usuarioServicio.findById(Integer.parseInt(session.getAttribute("IdUsuario").toString())).get() ;
		productos.setUsuarios(u);
		
		//Imagenes
		//condicion de cuando se crea un producto
		if (productos.getId() == null) { 
			String nombreImagen = subir.guardarImagenes(archivo);
			productos.setImgProducto(nombreImagen);
		}else { 
		}		
		productoServicio.guardar(productos);
		return "redirect:/productos";
	}
	
	@GetMapping("/editar/{Id}")
	public String editar(@PathVariable Integer Id,Model model) {
		Producto productos = new Producto();
		Optional<Producto> optionalProducto = productoServicio.get(Id) ;
		productos = optionalProducto.get();
		LOGGER.info("Producto buscado: {}", productos);
		model.addAttribute("producto", productos);
		return "productos/editar";
	}
	@PostMapping("/actualizar")
	public String actualizar(Producto productos, @RequestParam("img") MultipartFile archivo) throws IOException {
		
		
		//para editar producto sin cambiar imagen
		Producto p  = new Producto();
		p = productoServicio.get(productos.getId()).get();
		
		if (!archivo.isEmpty()) {
			productos.setImgProducto(p.getImgProducto());
		}else { // en el caso de editar imagen
			
			if (!p.getImgProducto().equals("default.jpg")) {
				subir.borrarImagen(p.getImgProducto());
			}
			String nombreImagen = subir.guardarImagenes(archivo);
			productos.setImgProducto(nombreImagen);
		}
		productos.setUsuarios(p.getUsuarios());
		productoServicio.actualizar(productos);	
		return "redirect:/productos";
	}
	
	@GetMapping("/borrar/{Id}")
	public String borrar(@PathVariable Integer Id) {
		Producto p = new Producto();
		p = productoServicio.get(Id).get();
		
		//es para eliminar la imagen(no la por defecto)
		if (!p.getImgProducto().equals("default.jpg")) {
			subir.borrarImagen(p.getImgProducto());
		}
		
		productoServicio.borrar(Id);
		return "redirect:/productos";
	}
	
}
